CREATE OR ALTER PROC SP_MOVIMIENTOS(
@ACCION				VARCHAR(30) = '',-- 'INSERT',
@FEC_INI			VARCHAR(50) = '',-- '06/05/2024',--'06/05/2024',
@FEC_FIN			VARCHAR(50) = '',-- '06/05/2024',--'06/05/2024',
@COD_CIA			VARCHAR(50) = '',-- 'CIA 1', --PK
@COMPANIA_VENTA_3	VARCHAR(50) = '',-- 'VENTA 1', --PK
@ALMACEN_VENTA		VARCHAR(50) = '',-- 'ALMACEN 1', --PK
@TIPO_MOVIMIENTO	VARCHAR(50) = '',-- 'TIPO MOV 1',--'TIPO MOV 1', --PK
@TIPO_DOCUMENTO		VARCHAR(50) = '',-- 'DNI',--'DNI', --PK
@NRO_DOCUMENTO		VARCHAR(50) = '',-- '72843917', --PK
@COD_ITEM_2			VARCHAR(50) = '',-- 'ITEM 1', --PK
@CANTIDAD			VARCHAR(50) = '',-- '50',
@PROVEEDOR			VARCHAR(50) = '',-- 'NICOLAS SAC',
@MONEDA				VARCHAR(50) = '' -- 'USD'
)
AS
BEGIN
	DECLARE @strDate VARCHAR(20)
	IF ISNULL(@FEC_INI,'') = ''
		SET @FEC_INI = '01/01/1960'
	IF ISNULL(@FEC_FIN,'') = ''
		SET @FEC_FIN = '01/01/2050'

	DECLARE @D_FEC_INI DATE = convert(date, @FEC_INI, 103)
	DECLARE @D_FEC_FIN DATE = convert(date, @FEC_FIN, 103)
	--SELECT @D_FEC_INI, @D_FEC_FIN


	SET @strDate = FORMAT(getdate(), 'dd/MM/yyyy')

	IF @ACCION = 'INSERT'
	BEGIN
		IF EXISTS (SELECT * FROM [dbo].[MOV_INVENTARIO]
					WHERE
						[COD_CIA] = @COD_CIA
					AND [COMPANIA_VENTA_3] = @COMPANIA_VENTA_3
					AND [ALMACEN_VENTA]	 = @ALMACEN_VENTA
					AND [TIPO_MOVIMIENTO] = @TIPO_MOVIMIENTO
					AND [TIPO_DOCUMENTO] = @TIPO_DOCUMENTO
					AND [NRO_DOCUMENTO] = @NRO_DOCUMENTO
					AND [COD_ITEM_2] = @COD_ITEM_2)
		BEGIN
			PRINT 'ERROR: Llave compuesta duplicada [COD_CIA], [COMPANIA_VENTA_3], [ALMACEN_VENTA], [TIPO_MOVIMIENTO], [TIPO_DOCUMENTO], [NRO_DOCUMENTO], [COD_ITEM_2]'
			RETURN
		END

		INSERT INTO [dbo].[MOV_INVENTARIO] ([COD_CIA],[COMPANIA_VENTA_3],[ALMACEN_VENTA],[TIPO_MOVIMIENTO],[NRO_DOCUMENTO],[COD_ITEM_2],[FECHA_TRANSACCION],[TIPO_DOCUMENTO],[CANTIDAD],[PROVEEDOR],[MONEDA])
			VALUES(@COD_CIA,@COMPANIA_VENTA_3,@ALMACEN_VENTA,@TIPO_MOVIMIENTO,@NRO_DOCUMENTO,@COD_ITEM_2,@strDate,@TIPO_DOCUMENTO,@CANTIDAD,@PROVEEDOR,@MONEDA)

		RETURN
	END

	IF @ACCION = 'UPDATE'
	BEGIN
		UPDATE [dbo].[MOV_INVENTARIO]
			SET CANTIDAD = @CANTIDAD
				,PROVEEDOR = @PROVEEDOR
				,MONEDA = @MONEDA
			WHERE [COD_CIA] = @COD_CIA
					AND [COMPANIA_VENTA_3] = @COMPANIA_VENTA_3
					AND [ALMACEN_VENTA]	 = @ALMACEN_VENTA
					AND [TIPO_MOVIMIENTO] = @TIPO_MOVIMIENTO
					AND [TIPO_DOCUMENTO] = @TIPO_DOCUMENTO
					AND [NRO_DOCUMENTO] = @NRO_DOCUMENTO
					AND [COD_ITEM_2] = @COD_ITEM_2

		RETURN
	END

	IF @ACCION = 'SELECT'
	BEGIN
		--SELECT a.FECHA_TRANSACCION fecha, [COD_CIA],[COMPANIA_VENTA_3],[ALMACEN_VENTA],[TIPO_MOVIMIENTO],[NRO_DOCUMENTO],[COD_ITEM_2],[FECHA_TRANSACCION],[TIPO_DOCUMENTO],[CANTIDAD],[PROVEEDOR],[MONEDA] FROM [dbo].[MOV_INVENTARIO] A ORDER BY [FECHA_TRANSACCION]

		SELECT [COD_CIA],[COMPANIA_VENTA_3],[ALMACEN_VENTA],[TIPO_MOVIMIENTO],[NRO_DOCUMENTO],[COD_ITEM_2],[FECHA_TRANSACCION],[TIPO_DOCUMENTO],[CANTIDAD],[PROVEEDOR],[MONEDA]	
			FROM [dbo].[MOV_INVENTARIO]
			WHERE convert(date, [FECHA_TRANSACCION], 103) BETWEEN @D_FEC_INI AND @D_FEC_FIN
				AND (ISNULL(@TIPO_MOVIMIENTO,'') = '' OR [TIPO_MOVIMIENTO] = @TIPO_MOVIMIENTO)
				AND (ISNULL(@NRO_DOCUMENTO,'') = '' OR [NRO_DOCUMENTO] = @NRO_DOCUMENTO)
				
		RETURN
	END



END

